import React, { useState } from "react";
import { drugsContract, contestAddress } from "../utils/links";
import Button from "@material-ui/core/Button";
import { useQuery } from "@apollo/client";
import { BITQUERY_INFLOW } from "../utils/links";
import winners from "../utils/pickWinners";

export default function RaffleForm() {
  const [address, setAddress] = useState(contestAddress);
  const [tokenContract, setTokenContract] = useState(drugsContract);
  const [winningAddresses, setWinningAddresses] = useState([]);
  const [totalTickets, setTotalTickets] = useState(0);
  const [tN, setTN] = useState({});
  const ticketsNormalized = {};

  const { loading, error, data } = useQuery(
    BITQUERY_INFLOW(address, tokenContract)
  );
  if (loading) return "Loading tx list...";
  if (error) return `Error! ${error.message}`;

  // format winner entries for beefy pickWinner
  data.ethereum.transfers.map(
    (tx) => (ticketsNormalized[tx.address.address] = Math.floor(tx.amount))
  );

  const handleSubmit = () => {
    setTN(ticketsNormalized);
    setTotalTickets(Object.values(ticketsNormalized).reduce((a, b) => a + b));
    const rand = Math.floor(Math.random() * Math.floor(totalTickets));
    // make list of individual ticket entries
    const list = [];
    const listOfTickets = Object.keys(ticketsNormalized).map((address) => {
      const amount = ticketsNormalized[address];
      for (let i = 1; i <= amount; i++) {
        list.push(address);
      }
    });
    // set winner and substrac 1 ticket
    const winningAddress = list[rand];
    setWinningAddresses([...winningAddresses, winningAddress]);
    console.log(ticketsNormalized[winningAddress]);
    ticketsNormalized[winningAddress] = ticketsNormalized[winningAddress] - 1;
    //    setTN(ticketsNormalized);
    console.log(totalTickets);
    console.log(ticketsNormalized[winningAddress]);
  };

  return (
    <center>
      <h1>Tickets {totalTickets}</h1>
      <br /> <br />
      <Button variant="contained" color="primary" onClick={handleSubmit}>
        WHO'S MY HOMIE???
      </Button>
      <br />
      <div>
        <br /> <br />
        {!loading ? (
          <table
            width="420px"
            style={{
              backgroundColor: "rgba(255,255,255,0.7)",
              border: "1px solid black",
            }}
          >
            <thead>
              <tr>
                <td>Winners!</td>
              </tr>
            </thead>
            <tbody>
              {Object.values(winningAddresses).map((address, i) => {
                return (
                  <tr key={address + i}>
                    <td>{i}</td>
                    <td>{address}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        ) : null}
      </div>
    </center>
  );
}
