import React, { useState, useEffect } from "react";
import { useQuery } from "@apollo/client";
import { BITQUERY_INFLOW, drugsContract, contestAddress } from "../utils/links";

export default function RaffleForm() {
  const [address, setAddress] = useState(0);
  const [ticketRatio, setTickerRatio] = useState(0);
  const [tokenContract, setTokenContract] = useState(0);
  const [hasInfo, sethasInfo] = useState(false);

  const addressHandler = (event) => setAddress(event.target.value);
  const ticketRatioHandler = (event) => setTickerRatio(event.target.value);
  const tokenContractHandler = (event) => setTokenContract(event.target.value);

  const { loading, error, data } = hasInfo
    ? useQuery(BITQUERY_INFLOW(contestAddress, drugsContract))
    : null;

  if (loading) return "Loading tx list...";
  if (error) return `Error! ${error.message}`;
  console.log(data);

  return (
    <form>
      <h1>50/50 Raffle</h1>
      <p align="left">
        contest address: {address}
        <br /> token:{tokenContract}
        <br /> ticket ratio:
        {ticketRatio}
      </p>
      <p align="left">
        Enter contest address:
        <input type="text" onChange={addressHandler} />
      </p>
      <p align="left">
        Enter ticket ratio (tokens/ticket):
        <input type="text" onChange={ticketRatioHandler} />
      </p>
      <p align="left">
        Enter contest address:
        <input type="text" onChange={tokenContractHandler} />
      </p>
    </form>
  );
}
