import React, { useState, useEffect } from "react";
import { useQuery } from "@apollo/client";
import { BITQUERY_INFLOW } from "../utils/links";

export default function AddressTXList({ address, tokenContract }) {
  const { loading, error, data } = useQuery(
    BITQUERY_INFLOW(address, tokenContract)
  );
  if (loading) return "Loading tx list...";
  if (error) return `Error! ${error.message}`;
  console.log(data.ethereum.transfers[0].amount);
  return (
    <table>
      <thead>
        <tr>
          <td>address</td>
          <td>amount</td>
        </tr>
      </thead>
      <tbody>
        {data.ethereum.transfers.map((tx) => {
          console.log(tx.address, tx.amount);
          <tr>
            <td>{tx.address.address}</td>
            <td>{tx.amount}</td>
          </tr>;
        })}
      </tbody>
    </table>
  );
}
