import React, { useState } from "react";
import { drugsContract, contestAddress } from "../utils/links";
import AddressTXList from "./AddressTXList";
import Button from "@material-ui/core/Button";
import { useQuery } from "@apollo/client";
import { BITQUERY_INFLOW } from "../utils/links";
import winners from "../utils/pickWinners";

export default function RaffleForm() {
  const [address, setAddress] = useState(contestAddress);
  const [ticketRatio, setTickerRatio] = useState(1);
  const [tokenContract, setTokenContract] = useState(drugsContract);
  const [numOfWinners, setNumOfWinners] = useState(1);
  const [winningAddressees, setWinningAddresses] = useState([]);

  const handleSubmit = () => {
    setNumOfWinners.push(
      winners(
        ticketList,
        0x6b18ddd481a4cf8ae7dc87d440627f5aa0f97ed2279d518b65da576be96b21a3,
        numOfWinners
      )
    );
  };

  const { loading, error, data } = useQuery(
    BITQUERY_INFLOW(address, tokenContract)
  );
  if (loading) return "Loading tx list...";
  if (error) return `Error! ${error.message}`;

  // format winner entries for beefy pickWinner
  const ticketList = {};
  data.ethereum.transfers.map(
    (tx) => (ticketList[tx.address.address] = tx.amount / ticketRatio)
  );

  return (
    <div>
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />winningAddressees}
      <br /> <br />
      {hasInfo ? (
        <div>
          <p>address: {address} </p>
          <p>contract: {tokenContract} </p>
          <p>ratio: {ticketRatio}</p>
          <p>winners: {numOfWinners}</p>
        </div>
      ) : null}
      <Button variant="contained" color="primary" onClick={handleSubmit}>
        get winner
      </Button>
      <br />
      <AddressTXList
        key={1}
        address={address}
        tokenContract={tokenContract}
        ticketRatio={ticketRatio}
        numOfWinners={numOfWinners}
      />
    </div>
  );
}
